mapper.select:
select * from sys_enumeration where 1=1
and (description like '%'+#{enumerationKeyword}+'%' or value like '%'+#{enumerationKeyword}+'%')
and value=#{value}
and description=#{description}


        select  
        * from (
        select row_number() over(order by id) as rn, * from 
        sys_enumeration
        where 1=1

			and (description like '%'+#{enumerationKeyword}+'%' or value like '%'+#{enumerationKeyword}+'%')


				and value=#{value} 


			and description=#{description} 

        )as a
        where 1=1


	            and rn>#{pageNum} and rn<=#{pageSize}

         select count(id) from sys_enumeration where 1=1

			and (description like '%'+#{enumerationKeyword}+'%' or value like '%'+#{enumerationKeyword}+'%')


			and value=#{value} 


			and description=#{description}
